import React, { Component } from 'react';
import { NavBar, NavBarTitle } from './Nav';


class About extends Component {

  state = {
      data: null,
      items:[],
      isLoaded: false
    }

  componentWillMount(){
    console.log('Initial call');
  }

  getData(){
    setTimeout(() => {
      console.log('Our data is fetched');
      this.setState({
        data: 'Hello WallStreet'
      })
    }, 1000)
  }

  componentDidMount(){
    this.getData();
    fetch('https://randomuser.me/api/')
      .then(res => res.json)
      .then(json => {
          this.setState({
              isLoaded:true,
              items:json,
          })
      })
  }

  render() {
      const {isLoaded, items} = this.state;
      let item = JSON.parse(items)
      console.log()

      if(!isLoaded){
          return(
              <div>Loading ....</div>
          )
      }
      else{
        return(
            <div>
            <NavBar 
                   left=<div/>
                   center=<NavBarTitle>{"About"}</NavBarTitle>
                   right=<div/> 
                   />    
                   <ul>
                   {console.log("items", items)}
                   {obj = JSON.parse(items)}
                       {/* {items.map(item => (
                           <li >
                             Name: {item.name}
                           </li>
                       ))}; */}
                    </ul>
            {this.state.data}
          </div>
          )
      }

  }
}

export default About;
