import React,{useState} from 'react';
import styled from 'styled-components';
import ReactPlayer from 'react-player';
import { NavBar, NavBarTitle } from './Nav';
import { StackCenter } from './Layout';
import { Button, StyledButton } from './Content';
import { ReactComponent as HeadsetTwoTone } from '../icons/HeadsetTwoTone.svg'
import { ReactComponent as Like } from '../icons/heart.svg'

const Text = styled.p`
   font-size: 16px;
   font-weight: 600;
   color: #034f6d;
   padding
`;

const LikeButton = styled.button`
  border: none;
  background: none;
  padding-left: 1rem;
`;

const officialTrailer = (props) => {

    const [count,setCount]=useState(0);

     const counting = () => {
         setCount(count+1);
     } 

    return(
        <div>
        <NavBar 
			 fixed
             left=<div/>
             center=<NavBarTitle>{"Trailers"}</NavBarTitle>
             right=<HeadsetTwoTone/> 
             />
            <StackCenter gridgap="0rem">
            <Text> Avengers Endgame </Text>
            <ReactPlayer 
              url='https://www.youtube.com/watch?v=ee1172yeqyE' 
              playing 
              width={"100%"}
              />
            </StackCenter>
            <LikeButton onClick={counting}>
            <Like style={{height:"2rem", width: "2rem"}}/>
            </LikeButton>
            <Text>{count} Liked</Text>
            </div>
    )
}

 export default officialTrailer;
