import React, { useState } from 'react';
import styled from 'styled-components';
import ReactPlayer from 'react-player';
import { NavBar, NavBarTitle } from './Nav';
import { StackCenter, CardGrid } from './Layout';
import { Button, StyledButton } from './Content';
import { ReactComponent as HeadsetTwoTone } from '../icons/HeadsetTwoTone.svg';
import { ReactComponent as Like } from '../icons/heart.svg';

const Text = styled.div`
    font-size: 16px;
	font-weight: 600;
	color: #034f6d;
	padding-right: ${props => (props.paddingRight ? props.paddingRight : 'none')};
`;

const LikeButton = styled.button`
	border: none;
	background: none;
	padding-right: 2rem;
`;

const officialTrailer = props => {
    const [count, setCount] = useState(0);
    const [subscribe,setSubscribe] = useState(false);

	const counting = () => {
        setCount(count + 1);
    };

    const subscription = () => {
        setSubscribe(true);
    }

	return (
		<div>
            <NavBar 
             fixed left=<div/> 
             center=<NavBarTitle>{'Trailers'}</NavBarTitle> 
             right=<HeadsetTwoTone/> 
             />
			 <StackCenter gridgap="0rem">
			   <Text> Avengers Endgame </Text>
               <ReactPlayer url="https://www.youtube.com/watch?v=ee1172yeqyE" 
                  playing 
                  width={'100%'} />
			 </StackCenter>
             <CardGrid>
			<LikeButton onClick={counting}>
				<Like style={{ height: '2rem', width: '2rem' }} />
                <Text>{count}</Text>
			</LikeButton>
            <div />
            <Button onClick={subscription}>{subscribe==true ? "subscribed" : "subscribe"}</Button>
            </CardGrid>
			{/* <Text paddingLeft="0.5rem">{count} Liked</Text> */}
            {console.log(subscribe)}
            {/* <Button onClick={subscription}>{subscribe==true ? "subscribed" : "subscribe"}</Button> */}
		</div>
	);
};

export default officialTrailer;
