import React,{useState} from 'react';
import styled from 'styled-components';
import ReactPlayer from 'react-player';
import { NavBar, NavBarTitle } from './Nav';
import { StackCenter } from './Layout';
import { Button, StyledButton } from './Content';
import { ReactComponent as HeadsetTwoTone } from '../icons/HeadsetTwoTone.svg'

const Text = styled.p`
   font-size: 16px;
   font-weight: 600;
   padding-left: 2rem;
   color: #034f6d;
`
const officialTrailer = (props) => {

    const [count,setCount]=useState(0);

     function counting () {
         setCount(count+1);
     } 

    return(
        <div>
        <NavBar 
			 fixed
             left=<div/>
             center=<NavBarTitle>{"Trailers"}</NavBarTitle>
             right=<HeadsetTwoTone/> 
             />
            <StackCenter gridgap="0rem">
            <Text> Avengers Endgame </Text>
            <ReactPlayer 
              url='https://www.youtube.com/watch?v=ee1172yeqyE' 
              playing 
              width={"100%"}
              />
              <p>{count} Likes</p>
              <button style={{width: "10rem", height:'2.5rem'}} onClick={counting} /> 
              <Button>Count</Button>
            </StackCenter>
            </div>
    )
}

 export default officialTrailer;
