import React from "react";
import styled from "styled-components";
import { NavBar, NavBarTitle } from './Nav';
import { StackCenter } from './Layout';
import Ratings from './Content';
import { Formik } from 'formik';
import { LabeledField } from './Form';


const Reviews = () => {
    console.log("hello");
    return(
        <div>
        <NavBar 
             left=<div/>
             center=<NavBarTitle>{"Reviews"}</NavBarTitle>
             right=<div/> 
             />
             <Formik
                  initialValues={{
                    email: ''
                  }}
                  onSubmit={(values, { setSubmitting }) => {
                    client
                      .query({
                        query: LookUpTeacher,
                        variables: values
                      })
                      .then(result => {
                        this.setState({
                          error:
                            'Please request an invitation from your teacher.'
                        });
                        setSubmitting(false);
                      })
                      .catch(err => console.log(`Error caught ${err}`));
                  }}
                  validationSchema={Yup.object().shape({
                    email: Yup.string()
                      .email(i18n._(t`Invalid`))
                      .required(i18n._(t`Required`))
                  })}
                  render={({ isSubmitting }) => {
                    return (
                      <Form>
                        <LabeledField
                          type="email"
                          name="email"
                          label={`Email`}
                          placeholder={`Email`}
                          required
                        />
                        <>
                          <button
                            primary
                            type="submit"
                            label={`Next`}
                            // disabled={isSubmitting ? 1 : 0}
                            // loading={isSubmitting ? 1 : 0}
                          />
                        </>
                        <Ratings />
                      </Form>
                    );
                  }}
                />

        <StackCenter>

            </StackCenter>
            </div>
    )
}

export default Reviews;
